import React, { useState, useContext } from 'react';
import QRCode from 'qrcode.react';

import InputData from './InputData'

import { DataContext } from '../Context/DataContext'

const QrCode = () => {

  const [show, setShow] = useState(false)
  const [concat, setConcat] = useState(null)
  const [numberInput, setNumberInput] = useState(0)
  const [validateInput, setValidateInput] = useState(0)

  const context = useContext(DataContext)

  const result = () => {
    setShow(!show)
    setConcat(context.data1 + context.data2 + context.data3)
    console.log(concat)
  }

  return (
    <div>

      nombre de champs: <input value={numberInput} onChange={setNumberInput(event.target.value)} /> <button onClick={() => setValidateInput(numberInput)} >valider</button>

      <InputData />

      <button onClick={() => result()} >générer</button>
      {show === true &&
        <QRCode value={concat} />
      }
    </div>
  )
}

export default QrCode
