import React, { useContext } from 'react'

import { DataContext } from '../Context/DataContext'

const InputData = (props) => {

  const context = useContext(DataContext)

  const handleChange = (e) => {
    if (context.testIndex === props.index) {
      if (e.target.name === "data1") {
        context.setData1(e.target.value)
        console.log(context.data1)
      } if (e.target.name === "data2") {
        context.setData2(e.target.value)
        console.log(context.data2)
      } if (e.target.name === "data3") {
        context.setData3(e.target.value)
        console.log(context.data3)
      }
    }
  }

  const resetData = () => {
    context.setData1(null)
    context.setData2(null)
    context.setData3(null)
  }

  const findIndex = () => {
    context.setTestIndex(props.index)
    console.log(context.testIndex)
    console.log(props)
  }

  return (
    <div>
      <input name="data1" type="text" value={context.data1} onChange={handleChange} onClick={() => findIndex()} />
      <input name="data2" type="text" value={context.data2} onChange={handleChange} onClick={() => findIndex()} />
      <input name="data3" type="text" value={context.data3} onChange={handleChange} onClick={() => findIndex()} />

      <button onClick={() => resetData()}>reset datas</button>
    </div>
  )
}

export default InputData
